const AutoRefresh = () => {
    const [loaded, setLoaded] = useState(false);

    useEffect(() => {
      const interval = setInterval(() => {
        setLoaded(true);
        window.location.reload();
      }, 5000); // Refresh every 5 seconds for testing

      return () => clearInterval(interval);
    }, []);

    return (
      <div>
