import {useEffect, useState } from 'react'

import AlarmDetails from './AlarmDetails';
import './System.css'


const System = () => {
    const [alarms, setAlarm] = useState(null)
    const [refreshFlag, setRefreshFlag] = useState(false);
    useEffect(() => {
        const fetchAlarmStatus = async() => {
            try {
                const response = await fetch('/api/alarmStatus/');

                // Check if the response is not okay (e.g., 404 or 500 errors)
                if (!response.ok) {
                    throw new Error('Network response was not ok.');
                }

                const json = await response.json();
                console.log(json);
                setAlarm(json);
            } catch (error) {
                console.error('Error fetching data:', error.message);
                // Handle the error (e.g., show a message to the user)
            }
        }
        fetchAlarmStatus()

    }, [])
    useEffect(() => {
        // Check for changes in the refreshFlag
        if (refreshFlag) {
          window.location.reload(); // Reload the page if refreshFlag changes
        }
      }, [refreshFlag]);



    const [isDarkMode, setIsDarkMode] = useState(false);


    useEffect(() => {
        // Check for saved preference in local storage
        const savedMode = localStorage.getItem('darkMode');
        if (savedMode) {
            setIsDarkMode(JSON.parse(savedMode));
        }
    }, []);

    useEffect(() => {
        // Update class on the body based on dark mode state
        document.body.classList.toggle('dark-mode', isDarkMode);
        document.body.classList.toggle('light-mode', !isDarkMode);

        // Save preference to local storage
        localStorage.setItem('darkMode', JSON.stringify(isDarkMode));
    }, [isDarkMode]);

    return (
        <div className='system'>
            <div className = 'statuses'>
                {alarms && alarms.map((alarm) => (
                    <AlarmDetails key={alarm._id} alarm={alarm} />
                ))}
                </div>
        </div>
    )
}

export default System
